class JobModels {
  final int id;
    final String title;
    final String coverPhoto;
    final String coverTag;
    final String coverTitle;
    final String category;
    final String content;
    final int author;
    final DateTime publishDate;
    final String blogDescription;
    final DateTime publishedDate;
    final dynamic updatedDate;
    final String status;
    final String remark;
    final String type;
    final int shareCount;
    final int userId;

    Blog({
        required this.id,
        required this.title,
        required this.coverPhoto,
        required this.coverTag,
        required this.coverTitle,
        required this.category,
        required this.content,
        required this.author,
        required this.publishDate,
        required this.blogDescription,
        required this.publishedDate,
        required this.updatedDate,
        required this.status,
        required this.remark,
        required this.type,
        required this.shareCount,
        required this.userId,
    });

    factory Blog.fromJson(Map<String, dynamic> json) => Blog(
        id: json["id"],
        title: json["title"],
        coverPhoto: json["cover_photo"],
        coverTag: json["cover_tag"],
        coverTitle: json["cover_title"],
        category: json["category"],
        content: json["content"],
        author: json["author"],
        publishDate: DateTime.parse(json["publish_date"]),
        blogDescription: json["blog_description"],
        publishedDate: DateTime.parse(json["published_date"]),
        updatedDate: json["updated_date"],
        status: json["status"],
        remark: json["remark"],
        type: json["type"],
        shareCount: json["share_count"],
        userId: json["user_id"],
    );
}
