import 'package:digitalksp/models/author/author_models.dart';
import 'package:digitalksp/models/blogs/blog_models.dart';
import 'package:digitalksp/models/categories_model.dart';
import 'package:digitalksp/models/jobs/jobs_models.dart';

import '../constants/urls.dart';

class SearchModel {
  final List<CategoriesModel> category;
  final List<AuthorModels> author;
  final List<BlogModels> blog;
  final List<JobModels> job;

  const SearchModel(
      {required this.category,
      required this.author,
      required this.blog,
      required this.job});

  factory SearchModel.fromJson(Map<String, dynamic> json) {
    return SearchModel(
      category: List<CategoriesModel>.from(
          json['data']['category'].map((data) => data)),
      author:
          List<AuthorModels>.from(json['data']['author'].map((data) => data)),
      blog: List<BlogModels>.from(json['data']['blogs'].map((data) => data)),
      job: List<JobModels>.from(json['data']['jobs'].map((data) => data)),
    );
  }
}

class SearchCategoryModel {
  final String id;
  final String name;
  const SearchCategoryModel({required this.id, required this.name});

  factory SearchCategoryModel.fromJson(Map<String, dynamic> json) {
    return SearchCategoryModel(
      id: json['id'].toString(),
      name: json['name'] ?? '',
    );
  }
}

class SearchAuthorModel {
  final String id;
  final String name;
  final String bio;
  final String profile;

  const SearchAuthorModel({
    required this.id,
    required this.name,
    required this.bio,
    required this.profile,
  });

  factory SearchAuthorModel.fromJson(Map<String, dynamic> json) {
    String imagePath = json['profile_picture'] ?? '';

    if (imagePath.startsWith('../')) {
      imagePath = imagePath.substring(3);
    }

    final imageUrl = imagePath.isNotEmpty
        ? '${ApiRequest.instance.IMAGE_URL_WITHOUT_UPLOADS}$imagePath'
        : '';

    return SearchAuthorModel(
      id: json['id'].toString(),
      name: json['name'] ?? '',
      bio: json['address'] ?? '',
      profile: imageUrl,
    );
  }
}

class SearchBlogModel {
  final String id;
  final String title;
  final String category;
  final String publishDate;

  const SearchBlogModel({
    required this.id,
    required this.title,
    required this.category,
    required this.publishDate,
  });

  factory SearchBlogModel.fromJson(Map<String, dynamic> json) {
    String imagePath = json['profile_picture'] ?? '';

    if (imagePath.startsWith('../')) {
      imagePath = imagePath.substring(3);
    }

    final imageUrl = imagePath.isNotEmpty
        ? '${ApiRequest.instance.IMAGE_URL_WITHOUT_UPLOADS}$imagePath'
        : '';

